import React, { useEffect, useState } from 'react'
import { Col, Form, Row } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import { listProducts } from '../../../actions/productActions'
import ProductByCategory from '../../Category/ProductByCategory'
import Loader from '../../Loader'

function UsersProductsScreen() {
    
    const dispatch = useDispatch()
    
    const {
        userReducers: { user },
        productReducers: { products, loading }
    } = useSelector((state) => state)

    const statuses = [
        {
            'name': 'Тривають',
            'value': 1,
            'id': 1
        },
        {
            'name': 'Закінчені',
            'value': 3,
            'id': 2
        },
        {
            'name': 'Всі позиції',
            'value': '',
            'id': 3
        }
    ]

    const [selectedStatus, setSelectedStatus] = useState('')

    useEffect(() => {
        if (selectedStatus && user){
            dispatch(listProducts({
                userID: user._id,
                status: selectedStatus
            }))
        } else if (user) {
            dispatch(listProducts({
                userID: user._id
            }))
        }
    }, [dispatch, user, selectedStatus])

    return (
        products &&
        <>
            <Row className='mx-3'>
                <Col md={4} className='fw-semibold'>
                    Ви маєте {products.length} {products.length === 1 ? 'product:': 'products:'}
                </Col>
                <Col md={8} className='d-flex flex-column justify-content-end mt-2 mt-md-0'>
                    <Row>
                        {statuses.map((status) => (
                            <Col>
                                {status.id !== 3 ?                                        
                                    <Form.Check
                                        type='radio'
                                        id={status.id.toString()}
                                        value={status.value.toString()}
                                        label={status.name}
                                        name='statusGroup'
                                        onChange={(e) => setSelectedStatus(e.target.value)}
                                    />                                        
                                :                                       
                                    <Form.Check
                                        defaultChecked
                                        type='radio'
                                        id={status.id.toString()}
                                        value={status.value.toString()}
                                        label={status.name}
                                        name='statusGroup'
                                        onChange={(e) => setSelectedStatus(e.target.value)}
                                        className='text-nowrap'
                                    />
                                }
                            </Col>
                        ))}
                    </Row>
                </Col>
            </Row>
            <Col className='d-flex justify-content-center'>
                <hr className='divider'/>
            </Col>   
            {loading ? <Loader /> :
            <Row className='mx-3'>
            {products.map((product) => (
                <Col key={product._id} sm={12} md={6} lg={4}>
                    <ProductByCategory product={product} />
                </Col>
            ))}
            </Row>
            }
        </>
    )
}

export default UsersProductsScreen
